import {Helmet} from "react-helmet";
import './Slider.css';
import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Carousel } from 'react-bootstrap';

const Footer = () => {
    const [index, setIndex] = useState(0);
    const handleSelect = (selectedIndex, e) => {
        setIndex(selectedIndex);
    };
    return (
        <Carousel activeIndex={index} onSelect={handleSelect} interval={3000}>
            <Carousel.Item>
                <img
                    className="d-block w-100"
                    src="https://next-images.123rf.com/index/_next/image/?url=https://assets-cdn.123rf.com/index/static/assets/top-section-bg.jpeg&w=3840&q=75"
                    alt="Slide 1"
                    style={{ height: '500px', objectFit: 'cover' }}
                />
                <Carousel.Caption style={{ backgroundColor: 'rgba(0, 123, 255, 0.7)', padding: '15px', borderRadius: '10px' }}>
                    <h5>Slide 1 Title</h5>
                    <p>This is the first slide description.</p>
                </Carousel.Caption>
            </Carousel.Item>

            <Carousel.Item>
                <img
                    className="d-block w-100"
                    src="https://www.shutterstock.com/image-photo/calm-weather-on-sea-ocean-600nw-2212935531.jpg"
                    alt="Slide 2"
                    style={{ height: '500px', objectFit: 'cover' }}
                />
                <Carousel.Caption style={{ backgroundColor: 'rgba(0, 123, 255, 0.7)', padding: '15px', borderRadius: '10px' }}>
                    <h5>Slide 2 Title</h5>
                    <p>This is the second slide description.</p>
                </Carousel.Caption>
            </Carousel.Item>

            <Carousel.Item>
                <img
                    className="d-block w-100"
                    src="https://media.istockphoto.com/id/1403500817/photo/the-craggies-in-the-blue-ridge-mountains.jpg?s=612x612&w=0&k=20&c=N-pGA8OClRVDzRfj_9AqANnOaDS3devZWwrQNwZuDSk="
                    alt="Slide 3"
                    style={{ height: '500px', objectFit: 'cover' }}
                />
                <Carousel.Caption style={{ backgroundColor: 'rgba(0, 123, 255, 0.7)', padding: '15px', borderRadius: '10px' }}>
                    <h5>Slide 3 Title</h5>
                    <p>This is the third slide description.</p>
                </Carousel.Caption>
            </Carousel.Item>
        </Carousel>
    );
};

//
//
export default Footer;
