import React from 'react';

const Hero = () => {
    return (
        <div
            className="hero"
            style={{
                background: "url('https://www.alxafrica.com/wp-content/uploads/2021/08/hero.jpg') center/cover",
                height: '100vh',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                color: 'white',
                textShadow: '2px 2px 5px rgba(0, 0, 0, 0.7)',
            }}
        >
            <div className="text-center">
                <h1>Empowering Africa's Future</h1>
                <p>Learn, grow, and connect with the best opportunities.</p>
                <a href="#" className="btn btn-primary btn-lg">Get Started</a>
            </div>
        </div>
    );
};

export default Hero;
